data.value:
select d.name
from sys.databases d
order by d.name;

--declare @ObjectName sysname = 'dbo.korm_Request_CheckBusinessLogic';
if object_id('tempdb..#weakObj') is null create table #weakObj (objName sysname not null);
IF EXISTS ( select 1 from #weakObj w where w.objName = @ObjectName ) BEGIN SELECT '<not avaible: weak object>' refName; END ELSE BEGIN SELECT DISTINCT ISNULL(re.referenced_schema_name, 'dbo') + '.' + re.referenced_entity_name + ISNULL('.' + re.referenced_minor_name, '') refName , re.referenced_database_name refDbName , o.[type] FROM sys.dm_sql_referenced_entities(@ObjectName, 'object') re LEFT JOIN sys.objects o ON o.[object_id] = re.referenced_id WHERE re.referenced_minor_id = 0; END;

--declare @TableName sysname = 'dbo.Table1'
declare @objid int;
select @objid = object_id(@TableName);
select refName = schema_name(schema_id) + '.' + o.name, refDbName = NULL, /* current db */ [type] = o.type from sys.objects o where parent_object_id = @objid and type IN ('TR','TA')

-- поиск "плохих" объектов
-- (на которые нельзя найти ссылки)

set nocount on;

if OBJECT_ID('tempdb..#weakObj') is not null
  drop table #weakObj;
create table #weakObj (objId int primary key, objName varchar(100) not null);

declare @objId int, @objName sysname;
declare @res table (r int not null);

declare cr cursor local
for
  select o.object_id objId
  , OBJECT_SCHEMA_NAME(o.object_id, DB_ID()) + '.' + o.name objName
  from sys.all_objects o
  where o.type in ('u', 'f', 'fn', 'p');

open cr;

while 1=1
begin
  fetch cr into @objId, @objName;
  if @@FETCH_STATUS<>0
    break;

  begin try

  insert @res(r)
  select 1
  from sys.dm_sql_referenced_entities(@objName, 'object') re;

  end try
  begin catch
    print @objName + ' (' + cast(@objId as varchar(50)) + ')';
    print error_message();


    insert #weakObj (objId, objName)
    values (@objId, @objName);

  end catch
end;

close cr;
deallocate cr;

--declare @ObjectName sysname; --set @ObjectName = '[dbo].[CustOrdersOrders]' --set @ObjectName = 'dbo.Orders';
declare @otype char(1); select top(1) @otype = o.type from sys.objects o where o.object_id = object_id(@ObjectName);
if @otype not in ('U') /* table */ begin SELECT sm.object_id , object_schema_name(sm.object_id) + '.' + OBJECT_NAME(sm.object_id) AS object_name , o.type , o.type_desc , sm.definition FROM sys.sql_modules AS sm JOIN sys.objects AS o ON sm.object_id = o.object_id where sm.object_id = object_id(@ObjectName) and o.type not in ('U') /* table */ end
if @otype = 'U' /* table */ begin with obj as ( select object_schema_name(o.object_id) + '.' + OBJECT_NAME(o.object_id) AS object_name , o.object_id , o.type , o.type_desc from sys.objects o where o.object_id = object_id(@ObjectName) ) , cols as ( select c.name col_name , t.system_type_id , t.name system_type_name , t.is_table_type , t.is_user_defined , c.is_identity , c.is_nullable , c.is_computed , c.is_xml_document , c.xml_collection_id , c.max_length , c.precision , c.scale , c.collation_name , c.column_id from obj o inner join sys.columns c on c.object_id = o.object_id left join sys.types t on t.user_type_id = c.user_type_id ) , sql_str as ( select [definition] = ( select c.col_name + ' ' + c.system_type_name + ' ' + case c.is_nullable when 1 then 'NOT NULL' else 'NULL' end + case c.is_identity when 1 then ' IDENTITY' else '' end + char(10) from cols c order by c.column_id for xml path('') ) ) select o.object_id , o.object_name , o.type , o.type_desc , definition = 'Table: ' + o.object_name + char(10) + 'Columns: ' + char(10) + s.definition from obj o cross join sql_str s;
end;

set nocount on --declare @ObjectName sysname = 'dbo.DescriptTable'
/* входящие объекты */ declare @objects table ( schemaName sysname not null, tableName sysname not null
primary key (schemaName, tableName) )
/* исходящие строки */ declare @output table ( schemaName sysname not null, tableName sysname not null, rownum bigint not null, rowtype varchar(50) not null, indent int default(0), --отступ при форматировании запроса sqltext varchar(max) null, endType tinyint default(0) --тип строки. 0-обычная, 1-после строки GO, 2-после строки запятая
primary key (schemaName, tableName, rownum) )
if object_id('tempdb..#ansi_params') is not null drop table #ansi_params;
if object_id('tempdb..#columns') is not null drop table #columns;
IF OBJECT_ID('tempdb..#colDefaults') IS NOT NULL DROP TABLE #colDefaults;
if object_id('tempdb..#index_name') is not null drop table #index_name;
if object_id('tempdb..#index_columns') is not null drop table #index_columns;
if object_id('tempdb..#fk_name') is not null drop table #fk_name;
if object_id('tempdb..#fk_cols') is not null drop table #fk_cols;
if object_id('tempdb..#table_chk') is not null drop table #table_chk;
--0. подготовка списка объектов insert @objects (schemaName, tableName) select schema_name(t.schema_id) sName, t.name tName from sys.tables t where t.object_id = object_id(@ObjectName) order by schema_name(t.schema_id), t.name
--1. определение параметров ANSI_NULLS, QUOTED_IDENTIFIER SELECT SCHEMA_NAME(tbl.schema_id) AS sName, tbl.name AS tName, tbl.uses_ansi_nulls AS AnsiNullsStatus, CASE WHEN 'FG'=dsidx.type THEN dsidx.name ELSE N'' END AS [FileGroup], CAST( case when tbl.is_ms_shipped = 1 then 1 when ( select major_id from sys.extended_properties where major_id = tbl.object_id and minor_id = 0 and class = 1 and name = N'microsoft_database_tools_support') is not null then 1 else 0 end AS bit) AS IsSystemObject, CAST(OBJECTPROPERTY(tbl.object_id,N'IsQuotedIdentOn') AS bit) AS QuotedIdentifierStatus into #ansi_params FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.indexes AS idx ON idx.object_id = tbl.object_id and idx.index_id < 2 LEFT JOIN sys.data_spaces AS dsidx ON dsidx.data_space_id = idx.data_space_id
insert @output ( schemaName, tableName, rownum, rowtype, sqltext, endType ) select sName, tName, 0, 'ANSI-params', case when AnsiNullsStatus=1 then 'SET ANSI_NULLS ON' else 'SET ANSI_NULLS OFF' end sqltext, 1 from #ansi_params
insert @output ( schemaName, tableName, rownum, rowtype, sqltext, endType ) select sName, tName, 1, 'ANSI-params', case when QuotedIdentifierStatus=1 then 'SET QUOTED_IDENTIFIER ON' else 'SET QUOTED_IDENTIFIER OFF' end sqltext, 1 from #ansi_params
--2. нужен ли SET ANSI_PADDING?
--3a. создание названия таблицы insert @output ( schemaName, tableName, rownum, rowtype, sqltext, endType ) select sName, tName, 5, 'tableHeader', 'CREATE TABLE [' +sName +'].[' +tName +'](', 0 from #ansi_params
insert @output ( schemaName, tableName, rownum, rowtype, sqltext, endType ) select sName, tName, 7000, 'tableFooter', ') ON [' +[FileGroup] +']', 1 from #ansi_params
--3b. создание столбцов таблицы SELECT SCHEMA_NAME(tbl.schema_id) AS [sName], tbl.name AS [tName], Row_Number() OVER (Partition by tbl.schema_id, tbl.name Order by clmns.column_id) AS [ID], clmns.name AS [colName], clmns.is_ansi_padded AS [AnsiPaddingStatus], clmns.is_computed AS [Computed], ISNULL(cc.definition,N'') AS [ComputedText], usrt.name AS [DataType], ISNULL(baset.name, N'') AS [SystemType], sclmns.name AS [DataTypeSchema], CAST(clmns.is_rowguidcol AS bit) AS [RowGuidCol], CAST(CASE WHEN baset.name IN (N'nchar', N'nvarchar') AND clmns.max_length <> -1 THEN clmns.max_length/2 ELSE clmns.max_length END AS int) AS [Length], CAST(clmns.precision AS int) AS [NumericPrecision], clmns.is_identity AS [Identity], CAST(ISNULL(ic.seed_value,0) AS bigint) AS [IdentitySeed], CAST(ISNULL(ic.increment_value,0) AS bigint) AS [IdentityIncrement], ISNULL(clmns.collation_name, N'') AS [Collation], CAST(clmns.scale AS int) AS [NumericScale], clmns.is_nullable AS [Nullable], CAST(ISNULL(cc.is_persisted, 0) AS bit) AS [IsPersisted], tbl.object_id as objectid INTO #columns FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.all_columns AS clmns ON clmns.object_id=tbl.object_id LEFT OUTER JOIN sys.computed_columns AS cc ON cc.object_id = clmns.object_id and cc.column_id = clmns.column_id LEFT OUTER JOIN sys.types AS usrt ON usrt.user_type_id = clmns.user_type_id LEFT OUTER JOIN sys.types AS baset ON baset.user_type_id = clmns.system_type_id and baset.user_type_id = baset.system_type_id LEFT OUTER JOIN sys.schemas AS sclmns ON sclmns.schema_id = usrt.schema_id LEFT OUTER JOIN sys.identity_columns AS ic ON ic.object_id = clmns.object_id and ic.column_id = clmns.column_id ORDER by sName, tName, ID
-- 3c. Default значения SELECT SCHEMA_NAME(tbl.schema_id) AS [sName], tbl.name AS [tName], clmns.name AS [colName], cstr.name AS [defaultName], CAST(cstr.is_system_named AS BIT) AS [IsSystemNamed], cstr.definition AS [Text] INTO #colDefaults FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.all_columns AS clmns ON clmns.object_id = tbl.object_id INNER JOIN sys.default_constraints AS cstr ON cstr.object_id = clmns.default_object_id ORDER BY sName, tName, colName
insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select c.sName, c.tName, 10 +c.ID,--rowid, 'columns', 1, '[' +c.colName +'] [' +c.DataType +'] ' + case when lower(c.DataType)='char' OR lower(c.DataType)='nchar' OR lower(c.DataType)='binary' then '(' +cast(c.[Length] as varchar) +') '
when lower(c.DataType)='varchar' OR lower(c.DataType)='nvarchar' OR lower(c.DataType)='varbinary' then '('+ case c.[Length] when -1 then 'max' else cast(c.[Length] as varchar) end + ') '
when lower(c.DataType)='numeric' OR lower(c.DataType)='decimal' then '(' +cast(c.NumericPrecision as varchar) +',' +cast(c.NumericScale as varchar) +') '
when lower(c.DataType)='float' then '(' +cast(c.NumericPrecision as varchar) +') '
else '' end + case when isnull(c.Collation, '')='' then '' else 'COLLATE ' +c.Collation +' ' end +
-- [DescriptTableId] [tinyint] IDENTITY(1,1) NOT NULL, CASE WHEN c.[Identity] = 1 THEN 'IDENTITY(' + LTRIM(c.IdentitySeed) + ',' + LTRIM(c.IdentityIncrement) + ') ' ELSE '' END + case when c.Nullable=0 then 'NOT NULL' else 'NULL' end +
-- [ModifiedDate] [datetime] NULL CONSTRAINT [DF_DescriptTable_ModifiedDate] DEFAULT (getdate()), -- [SupportedDirections] [varchar] (10) COLLATE Cyrillic_General_CI_AS NOT NULL DEFAULT ((1)) case when cd.colName is not null AND cd.[IsSystemNamed] = 1 then ' DEFAULT ' + cd.[Text] when cd.colName is not null AND cd.[IsSystemNamed] = 0 then ' CONSTRAINT ['+ cd.[defaultName] + '] DEFAULT ' + cd.[Text] else '' END + '' as sqltext, case when c.ID < cols.cnt then 2 else 0 end as rowEnd from #columns c outer apply ( select count(1) cnt from sys.all_columns ac (nolock) where ac.object_id = c.objectid ) cols LEFT JOIN #colDefaults cd ON cd.sName = c.sName AND cd.tName = c.tName AND cd.colName = c.colName
--4a. список индексов-ограничений (PK, UI) SELECT Row_Number() Over (Partition by tbl.schema_id, tbl.name order by i.index_id) rn, Row_Number() Over (Partition by tbl.schema_id, tbl.name, case when is_primary_key=0 and is_unique_constraint=0 then 0 else 1 end order by i.index_id) rn_for_comma, i.index_id, SCHEMA_NAME(tbl.schema_id) AS sName, tbl.name AS tName, i.name AS iName, CAST(ISNULL(k.is_system_named, 0) AS bit) AS [IsSystemNamed], i.is_primary_key + 2*i.is_unique_constraint AS [IndexKeyType], i.is_unique AS [IsUnique], CAST(CASE i.index_id WHEN 1 THEN 1 ELSE 0 END AS bit) AS [IsClustered], CASE WHEN 'PS'=dsi.type THEN dsi.name ELSE N'' END AS [PartitionScheme], --CAST(case when i.type=3 then 1 else 0 end AS bit) AS [IsXmlIndex], --case UPPER(ISNULL(xi.secondary_type,'')) when 'P' then 1 when 'V' then 2 when 'R' then 3 else 0 end AS [SecondaryXmlIndexType], CASE WHEN 'FG'=dsi.type THEN dsi.name ELSE N'' END AS [FileGroup], i.ignore_dup_key AS [IgnoreDuplicateKeys], i.fill_factor AS [FillFactor], CAST(INDEXPROPERTY(i.object_id, i.name, N'IsPadIndex') AS bit) AS [PadIndex], ~i.allow_row_locks AS [DisallowRowLocks], ~i.allow_page_locks AS [DisallowPageLocks], s.no_recompute AS [NoAutomaticRecomputation] INTO #index_name FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.indexes AS i ON (i.index_id > 0 and i.is_hypothetical = 0) AND (i.object_id=tbl.object_id) LEFT OUTER JOIN sys.key_constraints AS k ON k.parent_object_id = i.object_id AND k.unique_index_id = i.index_id LEFT OUTER JOIN sys.data_spaces AS dsi ON dsi.data_space_id = i.data_space_id --LEFT OUTER JOIN sys.xml_indexes AS xi ON xi.object_id = i.object_id AND xi.index_id = i.index_id LEFT OUTER JOIN sys.stats AS s ON s.stats_id = i.index_id AND s.object_id = i.object_id ORDER BY sName ASC, tName ASC, i.index_id ASC
/* шапка индекса */ insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select sName, tName, 2001 +20*(rn-1) as rownum, 'constr-index head' as rowtype, 1, 'CONSTRAINT [' +iName.iName +'] ' + case IndexKeyType when 1 then 'PRIMARY KEY ' when 2 then 'UNIQUE ' else '<error>' end + case IsClustered when 1 then 'CLUSTERED' else 'NONCLUSTERED' end + '(', 0 from #index_name iName where IndexKeyType >0
/* подвал индекса-ограничения: в последней строке запятую можно оставить. Выполняется на mssql2005 */ union all select sName, tName, 2018 +20*(rn-1) as rownum, 'constr-index footer' as rowtype, 1, ') '+ case [FillFactor] when 0 then '' else 'WITH (FILLFACTOR=' +cast([FillFactor] as varchar) +') ' end + 'ON [' +[FileGroup] +']', case rn_for_comma when indexes.cnt then 0 else 2 end from #index_name iName outer apply ( select count(1) cnt from #index_name i where i.sName = iName.sName AND i.tName = iName.tName AND IndexKeyType >0 ) indexes where IndexKeyType>0
--4b. столбцы индексов-ограничений SELECT SCHEMA_NAME(tbl.schema_id) AS sName, tbl.name AS tName, i.name AS iName, --(case ic.key_ordinal when 0 then cast(1 as tinyint) else ic.key_ordinal end) AS [ID], ic.key_ordinal ID, clmns.name AS colName, ic.is_included_column AS [IsIncluded], Row_Number() OVER ( Partition by tbl.[schema_id], tbl.name, i.name, ic.key_ordinal, ic.is_included_column Order by ic.index_column_id ) Included_ID, ic.is_descending_key AS [Descending], CAST(COLUMNPROPERTY(ic.object_id, clmns.name, N'IsComputed') AS bit) AS [IsComputed] INTO #index_columns FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.indexes AS i ON (i.index_id > 0 and i.is_hypothetical = 0) AND (i.object_id=tbl.object_id) INNER JOIN sys.index_columns AS ic ON (ic.column_id > 0 and (ic.key_ordinal > 0 or ic.partition_ordinal = 0 or ic.is_included_column != 0)) AND (ic.index_id=CAST(i.index_id AS int) AND ic.object_id=i.object_id) INNER JOIN sys.columns AS clmns ON clmns.object_id = ic.object_id and clmns.column_id = ic.column_id ORDER BY sName ASC, tName ASC, iName ASC,[ID] ASC
insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select iCols.sName, iCols.tName, 2001 +20 *(iName.rn -1) +iCols.ID, --iCols.ID начинается с 1 'constr-index column', 2, '[' +iCols.colName +'] ' + case iCols.Descending when 0 then 'ASC' else 'DESC' end, case when iCols.ID = colsCount.cnt then 0 else 2 end from #index_name iName INNER JOIN #index_columns iCols ON iCols.sName = iName.sName AND iCols.tName = iName.tName AND iCols.iName = iName.iName outer apply ( select count(1) cnt from #index_columns ic where ic.sName = iName.sName AND ic.tName = iName.tName AND ic.iName = iName.iName ) colsCount where iName.IndexKeyType>0
--5a. создание индексов
/* шапка индекса */ insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select sName, tName, 7001 +1042*(rn-1) as rownum, 'index head' as rowtype, 0, 'CREATE ' + case IsUnique when 1 then 'UNIQUE ' else '' end + case IsClustered when 1 then 'CLUSTERED ' else 'NONCLUSTERED ' end + 'INDEX [' +iName.iName +'] ON [' +iName.sName +'].[' +iName.tName +'] (', 0 from #index_name iName where IndexKeyType =0
/* шапка include-секции */ union all select sName, tName, 7018 +1042*(rn-1) as rownum, 'index include-section' as rowtype, 0, ') INCLUDE (', 0 from #index_name iName outer apply ( select count(1) cnt from #index_columns ic where ic.sName = iName.sName AND ic.tName = iName.tName AND ic.iName = iName.iName AND ic.isIncluded = 1 ) inclColumns where IndexKeyType =0 and inclColumns.cnt>0
/* подвал индекса */ union all select sName, tName, 8042 +1042*(rn-1) as rownum, 'index footer' as rowtype, 0, ') ' + case [FillFactor] when 0 then '' else 'WITH (FILLFACTOR=' +cast([FillFactor] as varchar) +') ' end + 'ON [' +[FileGroup] +']', 1 from #index_name where IndexKeyType=0
-- 5b. столбцы индекса /* ключевые поля */ insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select iCols.sName, iCols.tName, 7001 +1042 *(iName.rn -1) +iCols.ID, --iCols.ID начинается с 1 'index key-column', 1, '[' +iCols.colName +'] ' + case iCols.Descending when 0 then 'ASC' else 'DESC' end, case when iCols.ID = colsCount.cnt then 0 else 2 end from #index_name iName INNER JOIN #index_columns iCols ON iCols.sName = iName.sName AND iCols.tName = iName.tName AND iCols.iName = iName.iName outer apply ( select count(1) cnt from #index_columns ic where ic.sName = iName.sName AND ic.tName = iName.tName AND ic.iName = iName.iName AND ic.IsIncluded = 0 ) colsCount where iName.IndexKeyType=0 and iCols.IsIncluded = 0
union all /* include-поля индекса */ select iCols.sName, iCols.tName, 7019 +1042 *(iName.rn -1) +iCols.Included_ID, --iCols.IncludedID начинается с 1 'index key-column', 1, '[' +iCols.colName +']', case when iCols.Included_ID = colsCount.cnt then 0 else 2 end from #index_name iName INNER JOIN #index_columns iCols ON iCols.sName = iName.sName AND iCols.tName = iName.tName AND iCols.iName = iName.iName outer apply ( select count(1) cnt from #index_columns ic where ic.sName = iName.sName AND ic.tName = iName.tName AND ic.iName = iName.iName AND ic.IsIncluded = 1 ) colsCount where iName.IndexKeyType=0 and iCols.IsIncluded = 1
--6a. Внешние ключи /*внешние ключи: название*/ SELECT Row_Number() Over (Partition by tbl.schema_id, tbl.name order by cstr.object_id) rn, SCHEMA_NAME(tbl.schema_id) AS sName, tbl.name AS tName, cstr.name AS fkName, CAST(cstr.is_system_named AS bit) AS [IsSystemNamed], cstr.delete_referential_action AS [DeleteAction], cstr.update_referential_action AS [UpdateAction], rtbl.name AS [ReferencedTable], schema_name(rtbl.schema_id) AS [ReferencedTableSchema], ~cstr.is_not_trusted AS [IsChecked], ~cstr.is_disabled AS [IsEnabled], cstr.is_not_for_replication AS [NotForReplication] INTO #fk_name FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.foreign_keys AS cstr ON cstr.parent_object_id=tbl.object_id INNER JOIN sys.tables rtbl ON rtbl.object_id = cstr.referenced_object_id ORDER BY sName ASC, tName ASC, fkName ASC
/* шапка внешнего ключа */ insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select sName, tName, 268001 +2052 *(fkName.rn -1) as rownum, 'fkey header', 0, 'ALTER TABLE [' +sName +'].[' +tName +'] WITH ' + case IsChecked when 1 then 'CHECK ' else 'NOCHECK ' end + 'ADD CONSTRAINT [' +fkName +'] FOREIGN KEY (' as sqltext, 0 from #fk_name fkName
union all /* шапка перечисления столбцов таблицы, на которую ссылается внешний ключ */ select sName, tName, 269026 +2052 *(fkName.rn -1), 'fkey middle', 0, ') REFERENCES [' +ReferencedTableSchema +'].[' +ReferencedTable +'] (', 0 from #fk_name fkName
union all /* подвал внешнего ключа */ select sName, tName, 270051 +2052 *(fkName.rn -1), 'fkey footer', 0, ')', 1 from #fk_name fkName
--6b. внешний ключ: столбцы /* внешние ключи: определение */ SELECT SCHEMA_NAME(tbl.schema_id) AS sName, tbl.name AS tName, cstr.name AS fkName, fk.constraint_column_id AS ID, cfk.name AS colName, crk.name AS refColName INTO #fk_cols FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.foreign_keys AS cstr ON cstr.parent_object_id=tbl.object_id INNER JOIN sys.foreign_key_columns AS fk ON fk.constraint_object_id=cstr.object_id INNER JOIN sys.columns AS cfk ON fk.parent_column_id = cfk.column_id and fk.parent_object_id = cfk.object_id INNER JOIN sys.columns AS crk ON fk.referenced_column_id = crk.column_id and fk.referenced_object_id = crk.object_id ORDER BY sName ASC, tName ASC,fkName ASC, ID ASC
insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) /* столбцы таблицы, содержащей внешний ключ */ select fkName.sName, fkName.tName, 268001 +2052 *(fkName.rn -1) +fkCols.ID, --ID начинается с 1 'fkey columns', 1 as indent, '[' +fkCols.colName +']', case fkCols.ID when fkColCount.cnt then 0 else 2 end from #fk_name fkName INNER JOIN #fk_cols fkCols ON fkCols.sName = fkName.sName AND fkCols.tName = fkName.tName AND fkCols.fkName = fkName.fkName outer apply ( select count(1) cnt from #fk_cols fc where fc.sName = fkName.sName AND fc.tName = fkName.tName AND fc.fkName = fkName.fkName ) fkColCount
union all /* столбцы таблицы, на которую ссылается внешний ключ */ select fkName.sName, fkName.tName, 269026 +2052 *(fkName.rn -1) +fkCols.ID, --ID начинается с 1 'fkey ref-columns', 1 as indent, '[' +fkCols.refColName +']', case fkCols.ID when fkColCount.cnt then 0 else 2 end from #fk_name fkName INNER JOIN #fk_cols fkCols ON fkCols.sName = fkName.sName AND fkCols.tName = fkName.tName AND fkCols.fkName = fkName.fkName outer apply ( select count(1) cnt from #fk_cols fc where fc.sName = fkName.sName AND fc.tName = fkName.tName AND fc.fkName = fkName.fkName ) fkColCount
--6c. выполнение проверки внешнего ключа insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select sName, tName, 270052 +2052 *(fkName.rn -1), 'fkey check', 0, 'ALTER TABLE [' +sName +'].[' +tName +'] CHECK CONSTRAINT [' +fkName +']', 1 from #fk_name fkName
--9. создание ограничений (проверок) SELECT Row_Number() OVER (Partition by tbl.schema_id, tbl.name Order by cstr.name) rn, SCHEMA_NAME(tbl.schema_id) AS [sName], tbl.name AS [tName], cstr.name AS [constrName], cstr.is_not_for_replication AS [NotForReplication], ~cstr.is_not_trusted AS [IsChecked], ~cstr.is_disabled AS [IsEnabled], CAST(cstr.is_system_named AS bit) AS [IsSystemNamed], cstr.definition AS [Text] INTO #table_chk FROM sys.tables AS tbl INNER JOIN @objects ob ON ob.schemaName = SCHEMA_NAME(tbl.schema_id) AND ob.tableName = tbl.name INNER JOIN sys.check_constraints AS cstr ON cstr.parent_object_id=tbl.object_id ORDER BY [sName] ASC,[tName] ASC,[constrName] ASC
insert @output ( schemaName, tableName, rownum, rowtype, indent, sqltext, endType ) select sName, tName, 790001 +2*(rn-1), 'check constraint', 0, 'ALTER TABLE [' +sName +'].[' +tName +'] WITH '+ case IsChecked when 0 then 'NOCHECK ' else 'CHECK ' end + 'ADD CONSTRAINT [' +constrName +'] CHECK ' + case NotForReplication when 1 then 'NOT FOR REPLICATION ' else '' end + [Text], 1 from #table_chk
union all select sName, tName, 790002 +2*(rn-1), 'enable check constraint', 0, 'ALTER TABLE [' +sName +'].[' +tName +'] '+ case IsEnabled when 1 then 'CHECK ' else 'NOCHECK ' end + 'CONSTRAINT [' +constrName +']', 1 from #table_chk --select * from #ansi_params --select * from #columns --SELECT * FROM #colDefaults --select * from #index_name --select * from #index_columns --select * from #fk_name --select * from #fk_cols --select * from #table_chk
declare @v varchar(max), @v_fk varchar(max) select @v = '', @v_fk = '' select @v = @v + space(o.indent) + o.sqltext + case endtype when 0 then '' when 1 then char(13) +char(10) +'GO' when 2 then ',' end + char(13) +char(10) from @output o where left(rowtype,4)<>'fkey' order by schemaName, tableName, o.rownum
select @v_fk = @v_fk + space(o.indent) + o.sqltext + case endtype when 0 then '' when 1 then char(13) +char(10) +'GO' when 2 then ',' end + char(13) +char(10) from @output o where left(rowtype,4)='fkey' order by schemaName, tableName, o.rownum
select [definition] = @v +char(13) +char(10) + '---------------------------------------------------' +char(13) +char(10) + @v_fk
